function initialize(){var e={center:new google.maps.LatLng(40.0176,-105.2797),zoom:8};map=new google.maps.Map(document.getElementById("map-canvas"),e),google.maps.event.addListener(map,"click",function(e){placeMarker(e.latLng)})}function placeMarker(e){new google.maps.Marker({position:e,map:map})}var map;google.maps.event.addDomListener(window,"load",initialize);